composer transaction submit --card admin@aar -d '{"$class": "org.hyperledger.composer.system.AddAsset", "targetRegistry": "resource:org.hyperledger.composer.system.AssetRegistry#netsol.innovation.aar.token.model.Account", "resources": [{ "$class": "netsol.innovation.aar.token.model.Account", "address": "8d58cd59-8504-4e2f-acc7-ec324fd23bfe", "balance": 0 }]}'
