composer transaction submit --card admin@aar -d '{"$class": "org.hyperledger.composer.system.AddAsset", "targetRegistry": "resource:org.hyperledger.composer.system.AssetRegistry#netsol.innovation.aar.token.model.Account", "resources": [{ "$class": "netsol.innovation.aar.token.model.Account", "address": "2d61f75e-5c01-4359-bc42-9988393c1816", "balance": 0 }]}'
composer transaction submit --card admin@aar -d '{"$class": "org.hyperledger.composer.system.AddAsset", "targetRegistry": "resource:org.hyperledger.composer.system.AssetRegistry#netsol.innovation.aar.token.model.Account", "resources": [{ "$class": "netsol.innovation.aar.token.model.Account", "address": "8a50dedb-c1d0-43e3-abf8-8ef2bd08d922", "balance": 500 }]}'
composer transaction submit --card admin@aar -d '{"$class": "org.hyperledger.composer.system.AddAsset", "targetRegistry": "resource:org.hyperledger.composer.system.AssetRegistry#netsol.innovation.aar.token.model.Account", "resources": [{ "$class": "netsol.innovation.aar.token.model.Account", "address": "b79328bb-8f61-4c5c-93d8-f7a335c54459", "balance": 1000 }]}'
echo "Adding participants..."
composer participant add -c admin@aar -d '{ "$class": "netsol.innovation.aar.participants.model.Manufacturer", "bpId": "MF1", "title": "Manufacturer", "account": "resource:netsol.innovation.aar.token.model.Account#bcd5f08f-aeaf-4ed2-aeac-d73c618d4da7" }'
composer participant add -c admin@aar -d '{ "$class": "netsol.innovation.aar.participants.model.FinanceCompany", "bpId": "FC1", "title": "Finance Company", "account": "resource:netsol.innovation.aar.token.model.Account#8d58cd59-8504-4e2f-acc7-ec324fd23bfe" }'
