composer identity issue --card admin@aar -u manufacturer -a netsol.innovation.aar.participants.model.Manufacturer#MF1
composer identity issue --card admin@aar -u financecompany -a netsol.innovation.aar.participants.model.FinanceCompany#FC1
composer identity issue --card admin@aar -u dealer -a netsol.innovation.aar.participants.model.Dealer#DL1
