composer identity issue --card admin@aar -u fleetowner -a netsol.innovation.aar.participants.model.FleetOwner#FO1
echo "Adding one car for dev purposes.. VIN001"
composer transaction submit --card admin@aar -d '{"$class": "org.hyperledger.composer.system.AddAsset", "targetRegistry": "resource:org.hyperledger.composer.system.AssetRegistry#netsol.innovation.aar.token.model.Account", "resources": [{ "$class": "netsol.innovation.aar.token.model.Account", "address": "eef18513-9c70-488b-bfdc-6e8f6a478caa", "balance": 1 }]}'
composer transaction submit --card admin@aar -d '{"$class": "org.hyperledger.composer.system.AddAsset", "targetRegistry": "resource:org.hyperledger.composer.system.AssetRegistry#netsol.innovation.aar.vehicle.model.Vehicle", "resources": [{ "$class": "netsol.innovation.aar.vehicle.model.Vehicle", "vin": "VIN001", "locked": false, "vehicleDetails": { "$class": "netsol.innovation.aar.vehicle.model.VehicleDetails", "make": "Toyota", "model": "Camry" }, "account": "resource:netsol.innovation.aar.token.model.Account#eef18513-9c70-488b-bfdc-6e8f6a478caa", "oem": "resource:netsol.innovation.aar.participants.model.Manufacturer#MF1" }]}'